import {Component} from '@angular/core';
import {environment} from '../enviroments/environment';
import {TokenStorageService} from './service/token/token-storage.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  private role: string = '';

  authenticated = false;
  editAccess = false;
  login?: string;

  constructor(private tokenStorageService: TokenStorageService) {

  }

  ngOnInit(): void {
    this.authenticated = !!this.tokenStorageService.getToken();

    if (this.authenticated) {
      const user = this.tokenStorageService.getUser();
      this.role = user.role;
      this.login = user.login;

      this.editAccess = this.role == 'ADMIN';
    }
  }

  logout(): void {
    this.tokenStorageService.signOut();

    if (environment.production) {
      window.location.href = "/bank"
    } else {
      window.location.reload();
    }
  }

}
<div id="app">
  <nav class="navbar navbar-expand navbar-dark bg-dark">
    <a href="#" class="navbar-brand">SSAU</a>
    <ul class="navbar-nav mr-auto" routerLinkActive="active">
      <li class="nav-item">
        <a href="/home" class="nav-link" routerLink="home">Home </a>
      </li>
      <li class="nav-item" *ngIf="showEditor">
        <a href="/editor" class="nav-link" routerLink="editor">Book Editor</a>
      </li>
      <li class="nav-item">
        <a href="/shelf" class="nav-link" *ngIf="authenticated" routerLink="shelf">Shelf</a>
      </li>
    </ul>

    <ul class="navbar-nav ml-auto" *ngIf="!authenticated">
      <li class="nav-item">
        <a href="/register" class="nav-link" routerLink="register">Registration</a>
      </li>
      <li class="nav-item">
        <a href="/login" class="nav-link" routerLink="login">Login</a>
      </li>
    </ul>

    <ul class="navbar-nav ml-auto" *ngIf="authenticated">
      <li class="nav-item">
        <a href="/profile" class="nav-link" routerLink="profile">{{ login }}</a>
      </li>
      <li class="nav-item">
        <a href class="nav-link" (click)="logout()">Logout</a>
      </li>
    </ul>
  </nav>

  <div class="container">
    <router-outlet></router-outlet>
  </div>
</div>
