import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import { API_SERVICES, STORAGE_USER_KEY } from 'src/app/constants';
import { LoginParams, RegisterParams } from './types';
import { TokenStorageService } from '../token';

const httpOptions = {
  headers: new HttpHeaders({ 'Content-Type': 'application/json' }),
};

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  user: any = null;

  constructor(
    private http: HttpClient,
    private storageService: TokenStorageService
  ) {
    try {
      const storagedData = window.localStorage.getItem(STORAGE_USER_KEY);

      if (storagedData) {
        this.user = storagedData;
      }
    } catch (e) {
      console.log(e);
    }
  }

  get isLoggedIn(): boolean {
    return Boolean(this.user);
  }

  login(params: LoginParams): Observable<any> {
    return this.http.post(`${API_SERVICES.auth}/login`, params, httpOptions);
  }

  register(params: RegisterParams): Observable<any> {
    return this.http.post(`${API_SERVICES}/register`, params, httpOptions);
  }

  logout() {
    this.user = null;
    this.storageService.signOut();
  }
}
